<script>
import FilterItems from "@/components/Client/FilterItems.vue";
import SortItems from "@/components/Client/SortItems.vue";
import Card from "@/components/Admin/Card.vue";
import Clients from "@/components/Admin/Clients.vue";
import Suppliers from "@/components/Admin/Suppliers.vue";
export default {
  components: { FilterItems, SortItems, Card, Clients, Suppliers },
  data() {
    return {
      isFilterOpen: false,
      counter: 0,
      isTopMenuOpen: false,
      isEditOpen: false,
      showSort: false,
      activeElements: [
        { isActive: true },
        { isActive: false },
        { isActive: false },
      ],
    };
  },
  methods: {
    switchTab(idx) {
      this.isTopMenuOpen = false;
      this.activeElements.forEach((element) => (element.isActive = false));
      this.activeElements[idx].isActive = true;
    },
  },
  mounted() {
    const links = document.querySelectorAll(".logs-top__link");
    const catalogTitle = document.querySelector(".catalog-top__title > p");

    links.forEach((link, idx) => {
      link.addEventListener("click", () => {
        const text = link.querySelector("p");
        catalogTitle.textContent = text.textContent;
        this.switchTab(idx);
      });
    });
  },
};
</script>

